import { Component } from "@angular/core";
import { Item } from "./item";
import { HttpClient } from '@angular/common/http';

@Component({
  selector: "app-root",
  templateUrl: "./app.component.html",
  styleUrls: ["./app.component.css"],
})
export class AppComponent {
  newTodo : string = '';

  title = "todo-list-frontend";

  filter: "all" | "active" | "done" = "all";

  allItems = [];

  constructor(private http: HttpClient) { }

  ngOnInit(){
    this.http.get<any>('http://localhost:3000').subscribe({
      next: data => {
        this.allItems = data?.data;
      },
      error: error => {
        console.error(error);
        alert('Error querying');
      }
    })
  }

  get items() {

    return this.allItems;

    /*if (this.filter === "all") {
      return this.allItems;
    }
    return this.allItems.filter((item) =>
      this.filter === "done" ? item.done : !item.done
    );*/
  }

  addItem() {
    if (this.newTodo == '') {
      alert('Please enter a todo');
      return;
    }

    this.http.post<any>('http://localhost:3000', { todoValue: this.newTodo }).subscribe({
        error: error => {
          console.error(error);
          alert('Error saving');
        }
    })

    this.newTodo = '';
  }

  remove(item: Item) {
    this.http.delete<any>('http://localhost:3000/' + item._id).subscribe({
      error: error => {
        console.error(error);
        alert('Error deleting');
      }
  })
  }  
}
<!DOCTYPE html>
<html lang="en">
<head>
  <meta charset="UTF-8">
  <meta name="viewport" content="width=device-width, initial-scale=1.0">
  <title>Document</title>
</head>
<body>
  <div class="main">
    <h1>My To Do List</h1>
    <input
    type="text"
    class="form-control"
    placeholder="Enter todo"
    aria-label="Enter todo"
    aria-describedby="button-addon2"
    [(ngModel)]="newTodo"
  />
    <button class="btn-primary" (click)="addItem()">Add</button>
<div class="btn-wrapper">
  <button
    class="btn btn-menu"
    [class.active]="filter == 'all'"
    (click)="filter = 'all'">
    All
  </button>

  <button
    class="btn btn-menu"
    [class.active]="filter == 'active'"
    (click)="filter = 'active'">
    To Do
  </button>

  <button
    class="btn btn-menu"
    [class.active]="filter == 'done'"
    (click)="filter = 'done'">
    Done
  </button>
</div>

<!-- <h2>{{items.length}} item(s)</h2>
         <ul>... -->

    <h2>
      {{items.length}}
      <span *ngIf="items.length === 1; else elseBlock">item</span>
      <ng-template #elseBlock>items</ng-template>
    </h2>

    <ul>
      <li *ngFor="let i of items">
        <app-item (remove)="remove(i)" [item]="i"></app-item>
      </li>
    </ul>    
  </div>
</body>
</html>
